$(document).ready(function(){var noCommentError="No comments were seleceted. \n\nPlease select a comment from the table below and try again.";$("#publish-comment-btn, #unpublish-comment-btn").button();if($("#date-posted").length!=0){$("#date-posted").datetimepicker({showOn:"both",buttonImage:HTTP_IMAGE+"icons/admin/calendar_icon2.png",buttonImageOnly:true,ampm:true,dateFormat:"yy-mm-dd",timeFormat:"hh:mm TT"})}$("#toolbar-edit-comment, #action-edit-comment").click(function(){if(typeof boxesChecked==="undefined"||boxesChecked==0){alert(noCommentError)}else{CMS.submitButton("comment","edit")}});$("#toolbar-publish-comment, #action-publish-comment").click(function(){if(typeof boxesChecked==="undefined"||boxesChecked==0){alert(noCommentError)}else{CMS.submitButton("comment","publish")}});$("#toolbar-unpublish-comment, #action-unpublish-comment").click(function(){if(typeof boxesChecked==="undefined"||boxesChecked==0){alert(noCommentError)}else{CMS.submitButton("comment","unpublish")}});$("#toolbar-delete-comment, #action-delete-comment").click(function(){if(typeof boxesChecked==="undefined"||boxesChecked==0){alert(noCommentError)}else{CMS.submitButton("comment","delete")}});$(".publish-btn").click(function(){$("#state").val(1);CMS.submitButton("comment","save-close")});$(".unpublish-btn").click(function(){$("#state").val(0);CMS.submitButton("comment","save-close")});$("#toolbar-save-comment, #action-save-comment").click(function(){CMS.submitButton("comment","save")});$("#toolbar-save-new-comment, #action-save-new-comment").click(function(){CMS.submitButton("comment","save-new")});$("#toolbar-save-close-comment, #action-save-close-comment").click(function(){selectNavItem("#comments-link","#new-comment-link");CMS.submitButton("comment","save-close")});$("#toolbar-close-comment, #action-close-comment").click(function(){selectNavItem("#comments-link","#new-comment-link");window.location.href=HTTP_ADMIN+"comments"})});